create table hr.employee_performance (
    perf_id      number generated by default as identity not null enable,
    employee_id  number not null enable,
    evaluator_id number not null enable,
    perf_date    date default sysdate,
    rating       number(1, 0)
);

alter table hr.employee_performance add check ( rating between 0 and 5 ) enable;

alter table hr.employee_performance add primary key ( perf_id )
    using index enable;

