create table hr.employees (
    employee_id        number
        generated by default as identity
    not null enable,
    first_name         varchar2(50 byte) not null enable,
    last_name          varchar2(50 byte) not null enable,
    email              varchar2(100 byte) not null enable,
    hire_date          date not null enable,
    salary             number(10, 2) not null enable,
    last_salary_review date,
    fire_date          date,
    department_id      number,
    manager_id         number,
    job_id             number
);

alter table hr.employees
    add constraint chk_dates
        check ( fire_date is null
                or fire_date > hire_date ) enable;

alter table hr.employees add constraint chk_salary_positive check ( salary > 0 ) enable;

alter table hr.employees add primary key ( employee_id )
    using index enable;

alter table hr.employees add unique ( email )
    using index enable;


