create table hr.salary_history (
    history_id  number
        generated by default as identity
    not null enable,
    employee_id number not null enable,
    old_salary  number(10, 2),
    new_salary  number(10, 2),
    change_date date default sysdate,
    reason      varchar2(200 byte)
);

alter table hr.salary_history add primary key ( history_id )
    using index enable;


